INSERT INTO adrec.communityTheme (id, theme, theme_sub, name_author, url, img, title_post, text) VALUES (858, '지식·동향', '건강·의학', 'KB배관클리너', 'https://blog.naver.com/kimwondo', 'https://blogpfthumb-phinf.pstatic.net/MjAyMTA5MDhfMTQ4/MDAxNjMxMTA5NTMxOTkx.v5hrFkKwEr237jeqGr-Os4eJNlB9Bdpgz3zNt0hCcFsg.eQhR8EI1Ga6apsuQLFbMBomIcYtJ21XBNXRlU_i6I4Yg.JPEG.kimwondo/KakaoTalk_20210606_213824357_13.jpg?type=s1', '충남 공주시 탄천면 분배기밸브교환 및 옥천군 옥천읍 단독주택 보일러배관청소(밸브교환)', '안녕하세요. KB배관크리너 에이스케어입니다. 오늘은 두곳을 다녀온 내용을 포스팅 하도록 하겠습니다. 분배기 밸브 교환 먼저 방문한 곳은 공주시 탄천면 소재 농가주택에 심야전기 보일러를 사용하는 주택의 분배기 밸브의 부식문제로 먼저...');
